        Route(
            id = 14,
            number = 14,
            name = "Cala Galdana - Sant Tomàs",
            startPoint = "Cala Galdana",
            endPoint = "Sant Tomàs",
            distanceKm = 9.5,
            elevationGainMeters = 245,
            elevationLossMeters = 250,
            maxAltitudeMeters = 112,
